-- ----------------------------
--  Table structure for `bcc_ad_publish`
-- ----------------------------
DROP TABLE IF EXISTS `bcc_ad_publish`;
CREATE TABLE `bcc_ad_publish` (
  `id` int(11) NOT NULL,
  `ad_id` int(11) DEFAULT NULL COMMENT '对应素材ID',
  `device_id` int(11) DEFAULT NULL COMMENT '对应设备ID',
  `position` int(11) DEFAULT NULL COMMENT '广告位置',
