  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='图文广告管理';

-- ----------------------------
--  Table structure for `bcc_ad_report`
-- ----------------------------
DROP TABLE IF EXISTS `bcc_ad_report`;
CREATE TABLE `bcc_ad_report` (
  `id` int(11) NOT NULL,
  `publish_id` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL COMMENT '谁点击的',
