  `time` datetime DEFAULT NULL COMMENT '浏览时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `bcc_advertisement`
-- ----------------------------
DROP TABLE IF EXISTS `bcc_advertisement`;
CREATE TABLE `bcc_advertisement` (
  `id` int(11) NOT NULL,
  `file_path` varchar(45) DEFAULT NULL COMMENT '文件路径',
  `file_name` varchar(45) DEFAULT NULL COMMENT '文件名称',
  `state` int(11) DEFAULT NULL COMMENT '来源',
  `source` varchar(45) DEFAULT NULL,
  `industry_id` int(11) DEFAULT NULL COMMENT '行业分类',
  `is_third_party` varchar(45) DEFAULT NULL COMMENT '是否来自第三方',
  `company_id` varchar(45) DEFAULT NULL COMMENT '广告主',
  `url` varchar(500) DEFAULT NULL COMMENT '外链',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='图文广告素材管理';

